/* --- Global --- */
import { PropTypes } from "prop-types";
import { useForm } from "react-hook-form";
import { useMondayUpdateCreateMutation } from "@hooks";
import { useMondayBoardsQuery } from "@hooks";
import { withMonday, setToken } from "react-monday";
import { Box } from "theme-ui";
Box;

/**
 * @name FormModalTokenSet
 * @description Capture feedback from users.
 * @version 0.0.0
 */

const FormModalTokenSet = ({ monday, updateDefault, sx, ...props }) => {
  React.useEffect(() => {
    if (monday.token) {
      setValue("token", monday.token);
    }
  }, []);
  React.useEffect(() => {
    console.log(monday, "mondaymonday");
  }, [monday]);

  /* --- Form Hook State --- */
  const { handleSubmit, register, errors, setValue, formState } = useForm();

  /* --- Submit Handler --- */
  const onSubmit = async (values) => {
    if (values) {
      monday.setToken(values.token);
    }
  };

  /* --- Form : Component --- */
  return (
    <Box as="form" onSubmit={handleSubmit(onSubmit)}>
      <input
        name="token"
        placeholder="Token"
        register={register}
        errors={errors}
        sx={{}}
      />
      <Box
        as="button"
        sx={{ bg: "blue", color: "white", mt: 3, width: "100%" }}
      >
        {formState.isSubmitting & !formState.isSubmitted ? (
          <Box>{props.labelLoading}</Box>
        ) : (
          <Box>{props.label}</Box>
        )}

        {!formState.isSubmitting & formState.isSubmitted && (
          <Box>{props.labelComplete}</Box>
        )}
      </Box>
    </Box>
  );
};

FormModalTokenSet.defaultProps = {
  sx: {},
  label: "Submit",
  labelLoading: "Loading...",
  labelComplete: "Complete",
};

FormModalTokenSet.propTypes = {
  sx: PropTypes.object,
};

export default FormModalTokenSet;
